mapper.select:
SELECT *
FROM user
WHERE userPhoneNum = #{phoneNum} AND userPassword = #{password};

SELECT *
FROM user
WHERE userEmail = #{email} AND userPassword = #{password};

SELECT userNickname
FROM user
WHERE userId =#{id}

SELECT userId
FROM user
WHERE userNickname = #{nickname}

UPDATE user
SET userPassword = #{newPassword}
WHERE userId = #{id} AND userPassword = #{oldPassword};
